<?php

namespace MobileCart\CoreBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * OrderRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class OrderRepository
    extends EntityRepository
    implements CartRepositoryInterface
{

    /**
     * @return bool
     */
    public function isEAV()
    {
        return true;
    }

    /**
     * @return bool
     */
    public function hasImages()
    {
        return false;
    }

    /**
     * @return array
     */
    public function getSortableFields()
    {
        return [
            'status' => 'Status',
            'id' => 'ID',
            'total' => 'Total',
            'billing_name' => 'Name',
            'customer_id' => 'Customer ID',
            'created_at' => 'Created At',
        ];
    }

    /**
     * @return array
     */
    public function getFilterableFields()
    {
        return [
            [
                'code' => 'status',
                'label' => 'Status',
                'type' => 'string',
                'choices' => [
                    [
                        'value' => 'processing',
                        'label' => 'Processing',
                    ],
                    [
                        'value' => 'partially_shipped',
                        'label' => 'Partially Shipped',
                    ],
                    [
                        'value' => 'shipped',
                        'label' => 'Shipped',
                    ],
                ],
            ],
            [
                'code'  => 'email',
                'label' => 'Email',
                'type'  => 'string',
            ],
            [
                'code'  => 'billing_name',
                'label' => 'Billing Name',
                'type'  => 'string',
            ],
            [
                'code'  => 'billing_company',
                'label' => 'Billing Company',
                'type'  => 'string',
            ],
            [
                'code'  => 'total',
                'label' => 'Total',
                'type'  => 'number',
            ],
        ];
    }

    /**
     * @return mixed|string
     */
    public function getSearchField()
    {
        return ['email', 'billing_name', 'billing_company', 'shipping_name', 'shipping_company'];
    }

    /**
     * @return int|mixed
     */
    public function getSearchMethod()
    {
        return self::SEARCH_METHOD_LIKE;
    }
}
